import React, { useState } from "react";

export default function AddSubject({ subjects = [], onAddSubject }) {
  const [name, setName] = useState("");

  const handleAdd = (e) => {
    e.preventDefault();
    const trimmed = name.trim();
    if (!trimmed) return;
    onAddSubject && onAddSubject(trimmed);
    setName("");
  };

  return (
    <div className="bg-white dark:bg-gray-800 shadow rounded-2xl p-6">
      <h2 className="text-xl font-semibold mb-2">Add Subject</h2>
      <form onSubmit={handleAdd} className="space-y-3">
        <input
          type="text"
          placeholder="New subject name (e.g. Calculus)"
          value={name}
          onChange={(e) => setName(e.target.value)}
          className="w-full p-2 rounded border dark:bg-gray-700"
        />
        <div className="flex gap-2">
          <button type="submit" className="px-4 py-2 rounded bg-green-600 text-white hover:bg-green-700">
            Add Subject
          </button>
        </div>
      </form>

      {subjects.length > 0 && (
        <div className="mt-4 space-y-2">
          <h3 className="text-sm font-medium text-gray-500">Existing Subjects</h3>
          <div className="flex flex-wrap gap-2">
            {subjects.map((s) => (
              <span key={s} className="px-3 py-1 rounded bg-gray-200 text-sm">{s}</span>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}
