import React, { useState, useEffect } from "react";

import Timer from "./components/Timer";
import AddSubject from "./components/StudyForm";
import SubjectList from "./components/SubjectList";

export default function App() {
  const [sessions, setSessions] = useState(() => {
    const saved = localStorage.getItem("sessions");
    return saved ? JSON.parse(saved) : {};
  });

  useEffect(() => {
    localStorage.setItem("sessions", JSON.stringify(sessions));
  }, [sessions]);

  // duration is in seconds
  const addSession = (subject, content, notes, duration) => {
    setSessions((prev) => ({
      ...prev,
      [subject]: [
        ...(prev[subject] || []),
        {
          id: Date.now(),
          content,
          notes,
          duration,
          date: new Date().toLocaleString(),
        },
      ],
    }));
  };

  const [subjects, setSubjects] = React.useState(() => Object.keys(sessions));

  const addSubject = (name) => {
    setSubjects((s) => (s.includes(name) ? s : [...s, name]));
    // ensure an entry exists in sessions so lists are consistent
    setSessions((prev) => (prev[name] ? prev : { ...prev, [name]: [] }));
  };

  // site-wide background class (empty or one of 'study'|'break')
  const [siteBgClass, setSiteBgClass] = useState("");

  const handleStatusChange = ({ mode, isActive }) => {
    if (!isActive) return setSiteBgClass("");
    setSiteBgClass(mode === "study" ? "site-study" : "site-break");
  };

  return (
  <div className={`min-h-screen text-gray-800 dark:text-gray-100 p-6 bg-transition ${siteBgClass}`}>
      <header className="text-center mb-6">
        <h1 className="title">Pomolog ðŸ§ </h1>
        <p className="text-gray-500">Study smarter, track better.</p>
        <div className="mt-3">
          <button
            onClick={() => {
              if (window.confirm("Reset all study data? This will clear sessions and subjects.")) {
                setSessions({});
                setSubjects([]);
                localStorage.removeItem("sessions");
              }
            }}
            className="px-3 py-1 rounded bg-red-500 text-white"
            style={{ fontSize: "0.875rem" }}
          >
            Reset Data
          </button>
        </div>
      </header>

      <div className="max-w-3xl mx-auto space-y-8">
  <Timer onAddSession={addSession} subjects={subjects} />
  <AddSubject subjects={subjects} onAddSubject={addSubject} />
        <SubjectList sessions={sessions} />
      </div>
    </div>
  );
}
