import React, { useState, useEffect } from "react";
import Timer from "./components/Timer";
import StudyForm from "./components/StudyForm";
import SubjectList from "./components/SubjectList";

export default function App() {
  const [sessions, setSessions] = useState(() => {
    const saved = localStorage.getItem("sessions");
    return saved ? JSON.parse(saved) : {};
  });

  useEffect(() => {
    localStorage.setItem("sessions", JSON.stringify(sessions));
  }, [sessions]);

  const addSession = (subject, content, notes, duration) => {
    setSessions((prev) => ({
      ...prev,
      [subject]: [
        ...(prev[subject] || []),
        {
          id: Date.now(),
          content,
          notes,
          duration,
          date: new Date().toLocaleString(),
        },
      ],
    }));
  };

  return (
    <div className="min-h-screen bg-gray-100 text-gray-800 dark:bg-gray-900 dark:text-gray-100 p-6">
      <header className="text-center mb-6">
        <h1 className="title">Pomolog ðŸ§ </h1>
        <p className="text-gray-500">Study smarter, track better.</p>
      </header>

      <div className="max-w-3xl mx-auto space-y-8">
        <Timer />
        <StudyForm onAddSession={addSession} />
        <SubjectList sessions={sessions} />
      </div>
    </div>
  );
}
