import React, { useState } from "react";

export default function SubjectList({ sessions }) {
  const [activeSubject, setActiveSubject] = useState(null);

  const subjects = Object.keys(sessions);

  if (subjects.length === 0)
    return (
      <div className="text-center text-gray-500">
        No sessions logged yet. Start studying!
      </div>
    );

  return (
    <div className="bg-white dark:bg-gray-800 shadow rounded-2xl p-6">
      <h2 className="text-xl font-semibold mb-4">Study History</h2>

      <div className="space-y-2">
        {subjects.map((subject) => (
          <div key={subject}>
            <button
              onClick={() =>
                setActiveSubject(activeSubject === subject ? null : subject)
              }
              className="w-full text-left px-3 py-2 rounded bg-gray-200 dark:bg-gray-700 hover:bg-gray-300 dark:hover:bg-gray-600 font-medium"
            >
              {subject}
            </button>

            {activeSubject === subject && (
              <div className="pl-4 mt-2 space-y-2">
                {sessions[subject].map((s) => (
                  <div
                    key={s.id}
                    className="border-l-2 border-green-500 pl-3 text-sm"
                  >
                    <div>
                      <strong>{s.content}</strong>
                      <span className="text-gray-500 text-xs ml-2">{s.date}</span>
                    </div>
                    <div className="text-gray-400">{s.notes}</div>
                  </div>
                ))}
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}
