import React, { useState, useEffect } from "react";

// Timer component: simple, beginner-friendly implementation
export default function Timer({ onAddSession, subjects = [], onStatusChange }) {
  // durations in seconds
  const STUDY_SECONDS = 25 * 60;
  const BREAK_SECONDS = 5 * 60;

  // UI state
  const [mode, setMode] = useState("study"); // 'study' or 'break'
  const [seconds, setSeconds] = useState(STUDY_SECONDS);
  const [isActive, setIsActive] = useState(false);
  const [currentSubject, setCurrentSubject] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [modalSelect, setModalSelect] = useState("");
  const [showEndChoiceModal, setShowEndChoiceModal] = useState(false);
  const [showEndDetailsModal, setShowEndDetailsModal] = useState(false);
  const [endSections, setEndSections] = useState("");
  const [endNotes, setEndNotes] = useState("");

  // simple cycle counter (1,2,3...)
  const [cycles, setCycles] = useState(0);
  // store when the session started so we can pass it to addSession
  const [sessionStartTime, setSessionStartTime] = useState(null);

  // notify parent about status changes so the site background can update
  useEffect(() => {
    if (typeof onStatusChange === "function") {
      try {
        onStatusChange({ mode, isActive });
      } catch (e) {
        // ignore errors from parent
      }
    }
  }, [mode, isActive, onStatusChange]);

  // Diagnostic MP3 play helper: checks availability with HEAD and logs errors
  const tryPlayMp3 = async () => {
    const url = "/end-sound.mp3";
    try {
      console.log("Timer: checking for mp3 at", url);
      // try a HEAD request first; some static servers may not support HEAD
      const resp = await fetch(url, { method: "HEAD" });
      if (!resp.ok) {
        console.error("Timer: end-sound.mp3 not found or returned error", resp.status);
        // still attempt to play in case server doesn't support HEAD
      }
    } catch (err) {
      console.warn("Timer: HEAD check failed for end-sound.mp3", err);
    }

    try {
      const audio = new Audio(url);
      audio.volume = 0.7;
      audio.currentTime = 0;
      audio.addEventListener("play", () => console.log("Timer: audio started playing"));
      audio.addEventListener("error", (e) => console.error("Timer: audio element error", e));
      const p = audio.play();
      if (p && typeof p.then === "function") {
        p.then(() => console.log("Timer: play() resolved")).catch((err) => console.error("Timer: play() rejected", err));
      }
    } catch (e) {
      console.error("Timer: failed to create/play audio element", e);
    }
  };



  useEffect(() => {
    let interval = null;
    if (isActive && seconds > 0) {
      interval = setInterval(() => setSeconds((s) => s - 1), 1000);
    } else if (isActive && seconds === 0) {
      // If study finished naturally, count one cycle
      if (mode === "study") setCycles((c) => c + 1);
      // stop the running timer
      clearInterval(interval);
      setIsActive(false);
      // try to play a bundled MP3 first (place your file at public/end-sound.mp3)
      try {
        const audio = new Audio("/end-sound.mp3");
        audio.volume = 0.7;
        audio.currentTime = 0;
        audio.play().catch(() => {

        });
      } catch (e) {
        // ignore playback errors
      }

      // ask user whether to continue or end session
      setTimeout(() => {
        // show in-page modal to choose continue or end
        setShowEndChoiceModal(true);
      }, 200);
    }
    return () => clearInterval(interval);
  }, [isActive, seconds]);

  const toggle = () => setIsActive((v) => !v);
  const reset = () => {
    setIsActive(false);
    setSeconds(DEFAULT_SECONDS);
  };

  // Start with a prompt for subject when starting the timer (if study mode)
  const startWithPrompt = () => {
    if (mode === "study") {
      // show modal to enter subject (allow picking previous subjects)
      const list = subjects || [];
      const defaultSelect = currentSubject && list.includes(currentSubject) ? currentSubject : (list.length ? list[0] : "");
      setModalSelect(defaultSelect);
      setShowModal(true);
    } else {
      // break mode: start a short break immediately
      setSeconds(BREAK_SECONDS);
      setIsActive(true);
      if (typeof onStatusChange === "function") {
        try { onStatusChange({ mode: "break", isActive: true }); } catch (e) {}
      }
    }
  };

  // User confirmed the subject to start a session
  const submitModal = () => {
    const subject = (modalSelect || "").trim();
    if (!subject) return;
    setCurrentSubject(subject);
    // record start time as ISO string
    setSessionStartTime(new Date().toISOString());
    setShowModal(false);
    setSeconds(STUDY_SECONDS);
    setIsActive(true);
    if (typeof onStatusChange === "function") {
      try { onStatusChange({ mode: "study", isActive: true }); } catch (e) {}
    }
  };

  // format seconds as MM:SS
  const formatTime = (secondsLeft) => {
    const m = Math.floor(secondsLeft / 60);
    const sec = secondsLeft % 60;
    return `${m.toString().padStart(2, "0")}:${sec.toString().padStart(2, "0")}`;
  };

  // Progress ring calculations
  // progress ring math (kept simple)
  const total = mode === "study" ? STUDY_SECONDS : BREAK_SECONDS;
  const elapsed = total - seconds;
  const progress = seconds <= 0 ? 1 : elapsed / total; // 0..1

  const size = 400; // px
  const stroke = 10;
  const radius = (size - stroke) / 2;
  const circumference = 2 * Math.PI * radius;
  const dashOffset = circumference * (1 - progress);


  return (
  <div className={`bg-white dark:bg-gray-800 shadow rounded-2xl p-6 text-center bg-transition`}>
      <div className="timer-ring-wrap mx-auto mb-4" style={{ width: size, height: size }}>
        <svg className="timer-ring" width={size} height={size}>
          <defs>
            <filter id="ring-shadow" x="-50%" y="-50%" width="200%" height="200%">
              <feDropShadow dx="0" dy="2" stdDeviation="4" floodOpacity="0.08"/>
            </filter>
          </defs>
          <g transform={`translate(${size / 2}, ${size / 2})`}>
            <circle
              className="ring-bg"
              r={radius}
              fill="none"
              stroke="#eef2f7"
              strokeWidth={stroke}
            />
            <circle
              className="ring-progress"
              r={radius}
              fill="none"
              stroke="var(--accent)"
              strokeWidth={stroke}
              strokeLinecap="round"
              // use inline style so react updates attributes each render
              style={{
                strokeDasharray: circumference,
                strokeDashoffset: dashOffset,
                transition: "stroke-dashoffset 0.45s linear",
                transform: "rotate(-90deg)",
                transformBox: "fill-box",
                transformOrigin: "50% 50%",
              }}
            />
          </g>
        </svg>

        <div className="timer-center">
          <div className="text-7xl font-mono">{formatTime(seconds)}</div>
        </div>
      </div>

  <div className="space-x-4">
        {!currentSubject && (
          <button
            onClick={startWithPrompt}
            className="big-start px-6 py-4 rounded bg-blue-600 text-white hover:bg-blue-700"
          >
            Start Session
          </button>
        )}

        {currentSubject && (
          <>
            <button
              onClick={() => setIsActive((v) => !v)}
              className="px-4 py-2 rounded bg-yellow-500 text-white hover:bg-yellow-600"
            >
              {isActive ? "Pause" : "Resume"}
            </button>

            <button
              onClick={() => {
                // Next: switch between study and break
                if (mode === "study") {
                  // move to break and count a completed cycle
                  cyclesRef.current += 1;
                  setMode("break");
                  setSeconds(BREAK_SECONDS);
                  setIsActive(true);
                  if (typeof onStatusChange === "function") {
                    try { onStatusChange({ mode: "break", isActive: true }); } catch (e) {}
                  }
                } else {
                  // break mode: show end/continue modal
                  setShowEndChoiceModal(true);
                }
              }}
              className="px-4 py-2 rounded bg-gray-300 dark:bg-gray-700 hover:bg-gray-600 dark:hover:bg-gray-600"
            >
              Next
            </button>
          </>
        )}
      </div>
      {showModal && (
        <div className="modal-overlay">
          <div className="modal">
          <h3 className="text-xl font-semibold mb-2 text-gray-900 dark:text-gray-100">What subject will you be covering?</h3>
            {(subjects || []).length > 0 ? (
              <select
                value={modalSelect}
                onChange={(e) => {
                  const v = e.target.value;
                  setModalSelect(v);
                }}
                className="w-full p-2 rounded border mb-3 text-gray-900 dark:text-gray-100"
              >
                {subjects.map((s) => (
                  <option key={s} value={s} className="text-gray-900 dark:text-gray-100">{s}</option>
                ))}
              </select>
            ) : (
              <div className="mb-3 text-sm text-gray-500">No subjects yet. Use the "Add Subject" box to create one before starting a session.</div>
            )}
            <div className="flex justify-center gap-2">
              <button onClick={() => setShowModal(false)} className="px-4 py-2 rounded bg-gray-200 text-gray-900 dark:text-gray-100">Cancel</button>
              <button onClick={submitModal} disabled={(subjects || []).length === 0} className="px-4 py-2 rounded bg-blue-600 text-white" style={{ opacity: (subjects || []).length === 0 ? 0.5 : 1 }}>Start</button>
            </div>
          </div>
        </div>
      )}
      {showEndChoiceModal && (
        <div className="modal-overlay">
          <div className="modal">
            <h3 className="text-lg font-semibold mb-3 text-gray-900 dark:text-gray-100">Session finished</h3>
            <p className="text-sm text-gray-600 mb-4 text-gray-900 dark:text-gray-100">Would you like to continue this session or end it now?</p>
            <div className="flex justify-center gap-2">
              <button onClick={() => { setShowEndChoiceModal(false); setShowEndDetailsModal(true); }} className="px-4 py-2 rounded bg-gray-200 text-gray-900 dark:text-gray-100">End session</button>
              <button onClick={() => { setShowEndChoiceModal(false); setMode("study"); setSeconds(STUDY_SECONDS); setIsActive(true); }} className="px-4 py-2 rounded bg-blue-600 text-white">Continue</button>
            </div>
          </div>
        </div>
      )}

      {showEndDetailsModal && (
        <div className="modal-overlay">
          <div className="modal">
            <h3 className="text-lg font-semibold mb-3 text-gray-900 dark:text-gray-100">End session details</h3>
            <label className="text-sm text-gray-600">Sections Covered</label>
            <input value={endSections} onChange={(e) => setEndSections(e.target.value)} className="w-full p-2 rounded border mb-3 text-gray-900 dark:text-gray-100" />
            <label className="text-sm text-gray-600">Additional Notes</label>
            <textarea value={endNotes} onChange={(e) => setEndNotes(e.target.value)} className="w-full p-2 rounded border mb-3 text-gray-900 dark:text-gray-100" />
            <div className="flex justify-center gap-2">
              <button onClick={() => setShowEndDetailsModal(false)} className="px-4 py-2 rounded bg-gray-200 text-gray-900 dark:text-gray-100">Cancel</button>
              <button onClick={() => {
                if (onAddSession && currentSubject) {
                  // Calculate session duration in seconds
                  let duration = 25 * 60;
                  if (mode === "study") {
                    duration = STUDY_SECONDS - seconds;
                  } else if (mode === "break") {
                    duration = BREAK_SECONDS - seconds;
                  }
                  const cycles = cyclesRef.current || Math.floor(duration / (25 * 60));
                  const startTime = sessionStartTime || new Date().toISOString();
                  const endTime = new Date().toISOString();
                  onAddSession(currentSubject, endSections || "(no content)", endNotes || "", duration, startTime, endTime, cycles);
                }
                // ensure parent knows session ended
                if (typeof onStatusChange === "function") {
                  try { onStatusChange({ mode: "study", isActive: false }); } catch (e) {}
                }
                // clear session
                setShowEndDetailsModal(false);
                setShowEndChoiceModal(false);
                setCurrentSubject(null);
                setMode("study");
                setSeconds(STUDY_SECONDS);
                setIsActive(false);
                setEndSections("");
                setEndNotes("");
                // reset cycle counter and session start time for next session
                cyclesRef.current = 0;
                setSessionStartTime(null);
              }} className="px-4 py-2 rounded bg-green-600 text-white">Save & End</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
