import React, { useState, useEffect, useMemo } from "react";

export default function Timer({ onAddSession, subjects = [] }) {
  const DEFAULT_SECONDS = 25 * 60;
  const [seconds, setSeconds] = useState(DEFAULT_SECONDS);
  const [isActive, setIsActive] = useState(false);
  const [currentSubject, setCurrentSubject] = useState(null);

  useEffect(() => {
    let interval;
    if (isActive && seconds > 0) {
      interval = setInterval(() => setSeconds((s) => s - 1), 1000);
    } else if (seconds === 0) {
      clearInterval(interval);
    }
    return () => clearInterval(interval);
  }, [isActive, seconds]);

  const toggle = () => setIsActive(!isActive);
  const reset = () => {
    setIsActive(false);
    setSeconds(DEFAULT_SECONDS);
  };

  // Start with a prompt for subject when starting the timer
  const startWithPrompt = () => {
    if (!currentSubject) {
      const defaultSubject = subjects && subjects.length ? subjects[0] : "";
      const subject = window.prompt("What subject will you be covering?", defaultSubject);
      if (!subject) return; // user cancelled
      setCurrentSubject(subject.trim());
    }
    setIsActive(true);
  };

  const formatTime = (s) => {
    const m = Math.floor(s / 60);
    const sec = s % 60;
    return `${m.toString().padStart(2, "0")}:${sec.toString().padStart(2, "0")}`;
  };

  // Progress ring calculations
  const total = DEFAULT_SECONDS;
  const elapsed = total - seconds;
  const progress = seconds <= 0 ? 1 : elapsed / total; // 0..1

  const size = 400; // px
  const stroke = 10;
  const radius = (size - stroke) / 2;
  const circumference = 2 * Math.PI * radius;

  const dashOffset = useMemo(() => circumference * (1 - progress), [circumference, progress]);

  return (
    <div className="bg-white dark:bg-gray-800 shadow rounded-2xl p-6 text-center">
      <div className="timer-ring-wrap mx-auto mb-4" style={{ width: size, height: size }}>
        <svg className="timer-ring" width={size} height={size}>
          <defs>
            <filter id="ring-shadow" x="-50%" y="-50%" width="200%" height="200%">
              <feDropShadow dx="0" dy="2" stdDeviation="4" floodOpacity="0.08"/>
            </filter>
          </defs>
          <g transform={`translate(${size / 2}, ${size / 2})`}>
            <circle
              className="ring-bg"
              r={radius}
              fill="none"
              stroke="#eef2f7"
              strokeWidth={stroke}
            />
            <circle
              className="ring-progress"
              r={radius}
              fill="none"
              stroke="var(--accent)"
              strokeWidth={stroke}
              strokeLinecap="round"
              // use inline style so react updates attributes each render
              style={{
                strokeDasharray: circumference,
                strokeDashoffset: dashOffset,
                transition: "stroke-dashoffset 0.45s linear",
                transform: "rotate(-90deg)",
                transformBox: "fill-box",
                transformOrigin: "50% 50%",
              }}
            />
          </g>
        </svg>

        <div className="timer-center">
          <div className="text-5xl font-mono">{formatTime(seconds)}</div>
        </div>
      </div>

      <div className="space-x-4">
        <button
          onClick={() => (isActive ? setIsActive(false) : startWithPrompt())}
          className="px-4 py-2 rounded bg-blue-600 text-white hover:bg-blue-700"
        >
          {isActive ? "Pause" : "Start"}
        </button>
        <button onClick={reset} className="px-4 py-2 rounded bg-gray-300 dark:bg-gray-700 hover:bg-gray-400 dark:hover:bg-gray-600">
          Reset
        </button>
      </div>
    </div>
  );
}
