import React, { useState } from "react";

export default function StudyForm({ onAddSession }) {
  const [subject, setSubject] = useState("");
  const [content, setContent] = useState("");
  const [notes, setNotes] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!subject || !content) return;
    onAddSession(subject, content, notes, 25);
    setSubject("");
    setContent("");
    setNotes("");
  };

  return (
    <form
      onSubmit={handleSubmit}
      className="bg-white dark:bg-gray-800 shadow rounded-2xl p-6 space-y-4"
    >
      <h2 className="text-xl font-semibold mb-2">Log Study Session</h2>
      <input
        type="text"
        placeholder="Course / Subject"
        value={subject}
        onChange={(e) => setSubject(e.target.value)}
        className="w-full p-2 rounded border dark:bg-gray-700"
      />
      <input
        type="text"
        placeholder="Topic / Content Covered"
        value={content}
        onChange={(e) => setContent(e.target.value)}
        className="w-full p-2 rounded border dark:bg-gray-700"
      />
      <textarea
        placeholder="Notes (e.g. revisit Q5)"
        value={notes}
        onChange={(e) => setNotes(e.target.value)}
        className="w-full p-2 rounded border dark:bg-gray-700"
      />
      <button
        type="submit"
        className="w-full bg-green-600 text-white py-2 rounded hover:bg-green-700"
      >
        Save Session
      </button>
    </form>
  );
}
